import React, { useRef, useEffect, useContext } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {
  searchCompanies,
  targetRevenue,
  getAllCompany,
} from "../../actions/companyActions";

import "./TargetBar.css";

const TargetBar = ({ searchCompanies, targetRevenue, getAllCompany }) => {
  const [target, setTarget] = useContext(0);

  useEffect(() => {
    getAllCompany();
    //eslint-disable-next-line
  });

  const text = useRef("");

  const onChange = e => {
    searchCompanies(text.current.value);
  };

  const targetSearch = () => {
    targetRevenue(target);
  };

  return (
    <nav style={{ marginBottom: "30px" }} className='blue'>
      <div className='nav-wrapper'>
        <form>
          <div className='input-field'>
            <input
              id='search'
              type='search'
              placeholder="Type in a company's name or location"
              ref={text}
              onChange={onChange}
            />
            <label className='label-icon' htmlFor='search'>
              <i className='material-icons'>search</i>
            </label>
            <i className='material-icons'>close</i>
          </div>
          <div className='input-field'>
            <input
              id='targetSearch'
              type='search'
              ref={target}
              value={target === 0 ? null : target}
              placeholder='Type in a target revenue'
              onChange={setTarget(Number(value))}
            />
            <label className='label-icon' htmlFor='targetSearch'>
              <button onSubmit={targetSearch}>
                <i className='material-icons'>search</i>
              </button>
            </label>
            <i className='material-icons'>close</i>
          </div>
        </form>
      </div>
    </nav>
  );
};

TargetBar.propTypes = {
  searchCompanies: PropTypes.func.isRequired,
  targetRevenue: PropTypes.func.isRequired,
  getAllCompany: PropTypes.func.isRequired,
};

export default connect(null, { searchCompanies, targetRevenue, getAllCompany })(
  TargetBar,
);
