import {
  GET_ALL_COMPANY,
  GET_COMPANY,
  ADD_COMPANY,
  UPDATE_COMPANY,
  DELETE_COMPANY,
  COMPANY_ERROR,
  SET_LOADING,
  SET_CURRENT,
  CLEAR_CURRENT,
  SEARCH_COMPANY,
  TARGET_REVENUE,
  SET_SEARCH,
  CLEAR_SEARCH,
} from "../actions/types";

const initialState = {
  companies: [],
  company: [],
  current: null,
  loading: false,
  search: false,
  error: null,
};

const closestCompany = (companies, target) => {
  var closestCompany = companies;
  // Below lines are similar to insertion sort
  for (var i = 1; i < closestCompany.length; i++) {
    var diff = Math.abs(closestCompany[i] - target);

    // Insert closestCompany[i] at correct place
    var j = i - 1;
    if (Math.abs(closestCompany[j] - target) > diff) {
      var temp = closestCompany[i];
      while (j >= 0 && Math.abs(closestCompany[j] - target) > diff) {
        closestCompany[j + 1] = closestCompany[j];
        j--;
      }
      closestCompany[j + 1] = temp;
    }
  }
  console.log(closestCompany);
  return closestCompany;
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_LOADING:
      return {
        ...state,
        loading: true,
      };
    case GET_ALL_COMPANY:
      return {
        ...state,
        companies: action.payload,
      };
    case GET_COMPANY:
      return {
        ...state,
        company: action.payload,
      };
    case ADD_COMPANY:
      return {
        ...state,
        companies: [...state.companies, action.payload],
        loading: false,
      };
    case UPDATE_COMPANY:
      return {
        ...state,
        companies: state.companies.map(company =>
          company.id === action.payload.id ? action.payload : company,
        ),
        loading: false,
      };
    case DELETE_COMPANY:
      return {
        ...state,
        companies: state.companies.filter(
          company => company.id !== action.payload,
        ),
        loading: false,
      };
    case SEARCH_COMPANY:
      return {
        ...state,
        companies: action.payload,
      };
    case SET_SEARCH:
      return {
        ...state,
        search: true,
      };
    case CLEAR_SEARCH:
      return {
        ...state,
        search: false,
      };
    case TARGET_REVENUE:
      return {
        ...state,
        company: closestCompany([...state.companies], action.payload),
      };

    case SET_CURRENT:
      return {
        ...state,
        current: action.payload,
      };
    case CLEAR_CURRENT:
      return {
        ...state,
        current: null,
      };
    case COMPANY_ERROR:
      console.error(action.payload);
      return {
        ...state,
        error: action.payload,
      };
    default:
      return state;
  }
};
