import React, { useRef, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import {
  searchCompanies,
  targetRevenue,
  getAllCompany,
  setSearch,
  clearSearch,
} from "../../actions/companyActions";

import "./TargetBar.css";

const TargetBar = ({
  searchCompanies,
  targetRevenue,
  getAllCompany,
  setSearch,
  clearSearch,
}) => {
  useEffect(() => {
    getAllCompany();
    //eslint-disable-next-line
  }, []);

  const text = useRef("");
  const target = useRef("");

  const onChange = () => {
    searchCompanies(text.current.value);
  };

  const targetSearch = e => {
    setSearch();
    targetRevenue(Number(target.current.value));
    if (e.target.value === "") {
      clearSearch();
    }
  };

  return (
    <nav style={{ marginBottom: "30px" }} className='blue'>
      <div className='nav-wrapper'>
        <form>
          <div className='input-field'>
            <input
              id='search'
              type='search'
              placeholder="Type in a company's name or location"
              ref={text}
              onChange={onChange}
            />
            <label className='label-icon' htmlFor='search'>
              <i className='material-icons'>search</i>
            </label>
            <i className='material-icons'>close</i>
          </div>
          <div className='input-field'>
            <input
              id='targetSearch'
              type='search'
              ref={target}
              placeholder='Type in a target revenue'
              onChange={targetSearch}
            />
            <label className='label-icon' htmlFor='targetSearch'>
              <i className='material-icons'>search</i>
            </label>
            <i className='material-icons'>close</i>
          </div>
          <div class='fixed-action-btn information'>
            <a class='blue btn btn-large '>
              <i className='large material-icons'>info</i>
            </a>
            <ul>
              <li>
                <a
                  class='btn-floating modal-trigger btn-large red'
                  href='#status-modal'>
                  <i class='material-icons'>insert_chart</i>
                </a>
              </li>
              <li>
                <a class='btn-floating yellow darken-1'>
                  <i class='material-icons'>format_quote</i>
                </a>
              </li>
              <li>
                <a class='btn-floating green'>
                  <i class='material-icons'>publish</i>
                </a>
              </li>
              <li>
                <a class='btn-floating blue'>
                  <i class='material-icons'>attach_file</i>
                </a>
              </li>
            </ul>
          </div>
        </form>
      </div>
    </nav>
  );
};

TargetBar.propTypes = {
  searchCompanies: PropTypes.func.isRequired,
  targetRevenue: PropTypes.func.isRequired,
  getAllCompany: PropTypes.func.isRequired,
  setSearch: PropTypes.func.isRequired,
  clearSearch: PropTypes.func.isRequired,
};

export default connect(null, {
  searchCompanies,
  targetRevenue,
  getAllCompany,
  setSearch,
  clearSearch,
})(TargetBar);
