import React, { useRef, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { searchCompanies, getAllCompany } from "../../actions/companyActions";

const TargetBar = ({
  searchCompanies,
  getAllCompany,
  company: { companies },
}) => {
  const text = useRef("");

  useEffect(() => {
    getAllCompany();
    //eslint-desable-next-line
  })

  const onChange = e => {
    searchCompanies(text.current.value);
  };

  return (
    <nav style={{ marginBottom: "30px" }} className='blue'>
      <div className='nav-wrapper'>
        <form>
          <div className='input-field'>
            <input
              id='search'
              type='search'
              placeholder="Type in a company's name or location"
              ref={text}
              onChange={onChange}
            />
            <input
              id='search'
              type='search'
              placeholder='Type in a target revenue value'
            />
            <label className='label-icon' htmlFor='search'>
              <i className='material-icons'>search</i>
            </label>
            <i className='material-icons'>close</i>
          </div>
        </form>
      </div>
    </nav>
  );
};

TargetBar.propTypes = {
  searchCompanies: PropTypes.func.isRequired,
  getAllCompany: PropTypes.func.isRequired,
  company: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  company: state.company,
});

export default connect(mapStateToProps, { searchCompanies, getAllCompany })(
  TargetBar,
);
