import React from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { searchCompanies } from "../../actions/companyActions";

const TargetBar = ({ searchCompanies }) => {
  const text = useRef("");

  const onChange = e => {
    searchCompanies(text.current.value);
  };

  return (
    <nav style={{ marginBottom: "30px" }} className='blue'>
      <div className='nav-wrapper'>
        <form>
          <div className='input-field'>
            <input
              id='search'
              type='search'
              placeholder='Type in a target revenue value'
              ref={text}
              onChange={onChange}
            />
            <label className='label-icon' htmlFor='search'>
              <i className='material-icons'>search</i>
            </label>
            <i className='material-icons'>close</i>
          </div>
        </form>
      </div>
    </nav>
  );
};

TargetBar.propTypes = {
  searchCompanies: PropTypes.func.isRequired,
};

export default connect(null, { searchCompanies })(TargetBar);
