import React, { useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Preloader from "../layout/Preloader";
import Company from "./Company";
import { getAllCompany } from "../../actions/companyActions";
import { ltsSort, stlSort } from "../../actions/companyActions";

const Companies = ({
  company: { loading, companies, search, sortedCompanies },
  getAllCompany,
}) => {
  useEffect(() => {
    getAllCompany();
    // eslint-disable-next-line
  }, []);

  if (loading || companies === null) {
    return <Preloader />;
  }

  return (
    <ul className='collection with-header'>
      <li className='collection-header'>
        <nav>
          <div class='nav-wrapper blue'>
            <ul id='nav-mobile' class='left hide-on-med-and-down'>
              <li>
                <a className='btn-floating yellow darken-1' onClick={ltsSort}>
                  <i className='material-icons' style={chevronUp}>
                    chevron_left
                  </i>
                </a>
              </li>
              <li>
                <a className='btn-floating yellow darken-1' onClick={stlSort}>
                  <i className='material-icons' style={chevronDown}>
                    chevron_left
                  </i>
                </a>
              </li>
            </ul>
            <a href='#' class='brand-logo center'>
              Company List
            </a>
            <ul id='nav-mobile' className='right hide-on-med-and-down'>
              <li>
                <a
                  href='#add-company-modal'
                  className='btn-floating red modal-trigger'>
                  <i className='large material-icons'>add</i>
                </a>
              </li>
              <li>
                <a
                  className='blue btn-floating modal-trigger'
                  href='#status-modal'>
                  <i className='large material-icons'>info</i>
                </a>
              </li>
            </ul>
          </div>
        </nav>
      </li>
      {!loading && !search && companies.length === 0 ? (
        <p className='center'>No companies to show...</p>
      ) : search ? (
        sortedCompanies.map(com => <Company company={com} key={com._id} />)
      ) : (
        companies.map(comp => <Company company={comp} key={comp._id} />)
      )}
    </ul>
  );
};

Companies.propTypes = {
  companies: PropTypes.array.isRequired,
  sortedCompanies: PropTypes.array,
  search: PropTypes.bool.isRequired,
  getAllCompany: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  company: state.company,
});

const chevronUp = {
  transform: "rotate(90deg)",
};

const chevronDown = {
  transform: "rotate(-90deg)",
};

export default connect(mapStateToProps, { getAllCompany, stlSort, ltsSort })(
  Companies,
);
